rules.map:
function (doc) {
  var PATHSEP = "/";
  var isRule = function (doc) {  return (doc.trigger !== undefined) };
  if (isRule(doc)) try {
    var ns = doc.namespace.split(PATHSEP);
    var root = ns[0];
    emit([doc.namespace, doc.updated], 1);
    if (root !== doc.namespace) {
      emit([root, doc.updated], 1);
    }
  } catch (e) {}
}

packages-public.map:
function (doc) {
  var isPublicPackage = function(doc) { 
    return doc.binding && doc.publish && Object.keys(doc.binding).length == 0;
  }
  if (isPublicPackage(doc)) try {
    var value = {
      namespace: doc.namespace,
      name: doc.name,
      version: doc.version,
      publish: doc.publish,
      annotations: doc.annotations,
      updated: doc.updated,
      binding: false
    };
    emit([doc.namespace, doc.updated], value);
  } catch (e) {}
}

packages.map:
function (doc) {
  var isPackage = function (doc) {  return (doc.binding !== undefined) };
  if (isPackage(doc)) try {
    var value = {
      namespace: doc.namespace,
      name: doc.name,
      version: doc.version,
      publish: doc.publish,
      annotations: doc.annotations,
      updated: doc.updated
    };
    if (Object.keys(doc.binding).length > 0) {
      value.binding = doc.binding;
    } else {
      value.binding = false;
    }
    emit([doc.namespace, doc.updated], value);
  } catch (e) {}
}

actions.map:
function (doc) {
  var PATHSEP = "/";
  var isAction = function (doc) { return (doc.exec !== undefined) };
  if (isAction(doc)) try {
    var ns = doc.namespace.split(PATHSEP);
    var root = ns[0];
    var value = {
      namespace: doc.namespace,
      name: doc.name,
      version: doc.version,
      publish: doc.publish,
      annotations: doc.annotations,
      limits: doc.limits,
      exec: { binary: doc.exec.binary || false},
      updated: doc.updated
    };
    emit([doc.namespace, doc.updated], value);
    if (root !== doc.namespace) {
      emit([root, doc.updated], value);
    }
  } catch (e) {}
}

triggers.map:
function (doc) {
  var PATHSEP = "/";
  var isTrigger = function (doc) { return (doc.exec === undefined && doc.binding === undefined && doc.parameters !== undefined) };
  if (isTrigger(doc)) try {
    var ns = doc.namespace.split(PATHSEP);
    var root = ns[0];
    var value = {
      namespace: doc.namespace,
      name: doc.name,
      version: doc.version,
      publish: doc.publish,
      annotations: doc.annotations,
      updated: doc.updated
    };
    emit([doc.namespace, doc.updated], value);
    if (root !== doc.namespace) {
      emit([root, doc.updated], value);
    }
  } catch (e) {}
}
